import { Button, Container, TextField, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";
import React from "react";
import { useState } from "react";
const useStyles = makeStyles({
  btn: {
    backgroundImage:
      "linear-gradient(to right, #1FA2FF 0%, #12D8FA  51%, #1FA2FF  100%)",
    margin: "10px",
    padding: " 15px 45px",
    textAlign: "center",
    textTransform: " uppercase",
    transition: "0.5s",
    backgroundSize: "200% auto",
    color: "white",
    boxShadow: "0 0 20px #eee",
    borderRadius: " 10px",
    display: " block",

    "&:hover": {
      backgroundPosition:
        "right center" /* change the direction of the change here */,
      color: "#fff",
      textDecoration: "none",
    },
  },
});
const Todolist = () => {
  const [name, setName] = useState("");
  const [prize, setPrize] = useState("");
  const [nameError, setNameError] = useState(false);
  const [prizeError, setPrizeError] = useState(false);
  const classes = useStyles();
  const handleSubmit = () => {
    setNameError(false);
    setPrizeError(false);
    if (name && prize) {
      setNameError(false);
      setPrizeError(false);
      console.log("Name :", name);
    } else if (name === " ") {
      setNameError(true);
    } else if (prize === " ") {
      setPrizeError(true);
    }
  };
  return (
    <Container>
      <Typography variant="h3" gutterBottom component="h3" color="primary">
        Todo List
      </Typography>
      <form autoComplete="off">
        <TextField
          variant="outlined"
          id="name"
          label="Name"
          fullWidth
          required
          helperText="Please Enter Your Name"
          margin="dense"
          error={nameError}
        />
        <TextField
          variant="outlined"
          id="price"
          label="Price"
          fullWidth
          required
          helperText="Please Enter Your Name"
          margin="dense"
          error={prizeError}
        />
        <Button
          variant="contained"
          fullWidth
          className={classes.btn}
          onClick={handleSubmit}
        >
          Add List
        </Button>
      </form>
    </Container>
  );
};

export default Todolist;
