import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Paper,
  Button,
  Checkbox,
} from "@mui/material";
import { makeStyles } from "@mui/styles";
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import { Pill } from "evergreen-ui";
import React from "react";
import { useState } from "react";

const useStyles = makeStyles({
  title: {
    marginTop: 50,
    color: "#1FA2FF ",
  },
});
const DataHolder = ({ data, onDelete }) => {
  const classes = useStyles();
  //   console.log(data);
  const [addValue, setAddValue] = useState([]);
  const [total, setTotal] = useState();
  const addValue_array = (array_data) => {
    setAddValue(array_data.price);
    const value = array_data.price;
    const check_data = {
      value,
    };
    setAddValue([...addValue, check_data]);
    console.log(addValue);
    var sum = 0;
    for (var i = 0; i < addValue.length; i++) {
      var sum = sum + parseInt(addValue[i].value);
    }
    setTotal(sum);
  };
  const remove_value = (data) => {
    console.log(data);
    const remove_value = data.price;
    setTotal(total - remove_value);
  };
  return (
    <React.Fragment>
      <Typography
        variant="h3"
        // color="primary"
        component="h3"
        className={classes.title}
      >
        Todo DataHolder
      </Typography>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <caption>
            <b>
              <Button variant="contained">Total</Button>:
              <Pill color="blue">5</Pill> <br></br>
            </b>
          </caption>
          <TableHead>
            <TableRow>
              <TableCell>Sr.no</TableCell>
              <TableCell align="right">Name</TableCell>
              <TableCell align="right">Price&nbsp;(â‚¹)</TableCell>
              <TableCell align="right">Select</TableCell>
              <TableCell align="right">Delete</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((data, index) => (
              <TableRow
                key={data.name}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {index + 1}
                </TableCell>
                <TableCell align="right">{data.name}</TableCell>
                <TableCell align="right">{data.price}</TableCell>
                <TableCell align="right">
                  {
                    <Checkbox
                      onChange={(e) => {
                        if (e.target.checked) {
                          console.log("is checked");
                          addValue_array(data);
                        } else {
                          remove_value(data);
                        }
                      }}
                      size="small"
                    />
                  }
                </TableCell>
                <TableCell align="right">
                  <Button
                    type="button"
                    variant="contained"
                    onClick={() => {
                      onDelete(index);
                    }}
                    endIcon={<DeleteOutlineIcon />}
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </React.Fragment>
  );
};

export default DataHolder;
